        //     </footer>
        // </Main>
        <div style="display:flex,
       ">
            <h1>
                Login
            </h1>
            <button type="button" onClick={handleLogin}>
                Logar
            </button>
            <button type="button" onClick={handleCadastro}>
                Cadastrar
            </button>
        </div>
    )
}

export default Login;
