import React,{useContext,useState} from "react";

import {UsuarioContext,} from '../../context/user';

 import {  
     Main,
     Header,
     Content,
     Footer,
     ContainerLogoPharse,
     ContainerSignIn,

 } from "./styles";

function Login() {

    const {signIn, signUp} = useContext(UsuarioContext);
    console.log('=================>',document.getElementById("#inputEmail")); 
    console.log('=================>',document.getElementById("#inputSenha"));
    const [email,setEmail] = useState(document.getElementById("#inputEmail"));
    const [password,setpassword] = useState(document.getElementById("#inputSenha"));
    
    const handleLogin = async () => {
        try{
            await signIn(email,password);
        }catch(err){
            console.warn(err);
        }
    }

    const handleCadastro = async () => {
        try{
            await signUp(email,password);
        }catch(err){
            console.warn(err);
        }
    }
    return (
        <Main>
            <Header>
              
            </Header>

            <Content>
               <ContainerLogoPharse>
                <div id="logo">
                    <img src = "/images/olimpiada.png" width='150px'/>
                </div>
                <div id="pharse">
                    <p>Problemas para formar</p>
                    <p>um grupo de trabalho?</p>
                    <p>O Grupou! Resolve!</p>
                </div>
               </ContainerLogoPharse>
               <ContainerSignIn>
                    <div id="conteinerLogin">
                        <div id="conteinerButtons">
                            <div class="button inativo">
                                Membro
                            </div>
                            <div class="button inativo">
                                Convidado
                            </div>
                        </div>
                        <div class="box">
                            <div class="title">
                                E-mail
                            </div>
                            <input id="inputEmail" placeholder="Digite seu e-mail" />
                        </div>
                        <div class="box">
                            <div class="title">
                                Senha
                            </div>
                            <input id="inputSenha" placeholder="Digite sua senha" />
                        </div>
                        <div class="forgotCredentials">
                            Esqueci minha senha
                        </div>
                        <div id="conteinerLoginOrJoin">
                            <div class="button inverte" onClick={handleCadastro}>
                                Cadastre-se
                            </div>
                            <div class="button inverte" onClick={handleLogin}>
                                Entrar
                            </div>
                        </div>
                    </div>
               </ContainerSignIn>
            </Content>

            <Footer>
               <div id="logo">
                    <img src = "/images/olimpiada.png" width='150px'/>
               </div>
               <div id="about">
                    <p>
                        Contato
                    </p>
                    <p>
                        contato@contato.com
                    </p>
               </div>
               <div id="social">
                    <img src = "/images/Facebook.png" width='50px'/>
                    <img src = "/images/Instagram.png" width='50px'/>
                    <img src = "/images/Linkedin.png" width='50px'/>
               </div>
            </Footer>
        </Main>
    )
}

export default Login;
