import {UsuarioContext,} from '../../context/user';

 import {  
     Main,
     Header,
     Content,
     Footer,
     ContainerLogoPharse,
     ContainerSignIn,

 } from "./styles";

function Login() {

    const {signIn, signUp} = useContext(UsuarioContext);
    const [email,setEmail] = useState('teste@gmail.com');
    const [password,setpassword] = useState('123456');
    
    const handleLogin = async () => {
        try{
            await signIn(email,password);
        }catch(err){
            console.warn(err);
        }
    }

    const handleCadastro = async () => {
        try{
            await signUp(email,password);
        }catch(err){
            console.warn(err);
        }
    }
    return (
        <Main>
            <Header>
                <div id="logo">
                    <img src="/public/images/logo_white.png" width="300px"/>
                </div>
            </Header>

            <Content>
               <ContainerLogoPharse>

               </ContainerLogoPharse>
               <ContainerSignIn>

               </ContainerSignIn>
            </Content>

            <Footer>
               
            </Footer>
        </Main>

